package com.zhzx.dao.bean.prod;import com.zhzx.dao.bean.BaseBean;import java.util.ArrayList;import java.util.List;public class ProdInfoDetail extends ProdInfo {		/**	 * 商品属性集合	 */	private List<ProdProperty> prodPropertys;	/**	 * 商品评论集合	 */	private List<ProdComment> prodComments;	public List<ProdProperty> getProdPropertys() {		if (this.prodPropertys == null){			this.prodPropertys = new ArrayList<ProdProperty>();		}		return prodPropertys;	}	public void setProdPropertys(List<ProdProperty> prodPropertys) {		this.prodPropertys = new ArrayList<ProdProperty>();		this.prodPropertys.addAll(prodPropertys);	}	public List<ProdComment> getProdComments() {		if (this.prodComments == null){			this.prodComments = new ArrayList<ProdComment>();		}		return prodComments;	}	public void setProdComments(List<ProdComment> prodComments) {		this.prodComments = new ArrayList<ProdComment>();		this.prodComments.addAll(prodComments);	}}