package com.zhzx.dao.bean.prod;import com.zhzx.dao.bean.BaseBean;public class ProdPlan extends BaseBean {		/**	* 活动策划表	*/	private String id;		/**	*活动名称	*/	private String planName;		/**	*活动描述	*/	private String planDesc;		/**	*活动状态（活动有效：1，活动失效：0）	*/	private String status;		/**	*活动开始时间	*/	private String startTime;		/**	*活动结束时间	*/	private String endTime;		/**	*创建时间	*/	private String createTime;			public String getId() {	    return this.id;	}	public void setId(String id) {	    this.id=id;	}	public String getPlanName() {	    return this.planName;	}	public void setPlanName(String planName) {	    this.planName=planName;	}	public String getPlanDesc() {	    return this.planDesc;	}	public void setPlanDesc(String planDesc) {	    this.planDesc=planDesc;	}	public String getStatus() {	    return this.status;	}	public void setStatus(String status) {	    this.status=status;	}	public String getStartTime() {	    return this.startTime;	}	public void setStartTime(String startTime) {	    this.startTime=startTime;	}	public String getEndTime() {	    return this.endTime;	}	public void setEndTime(String endTime) {	    this.endTime=endTime;	}	public String getCreateTime() {	    return this.createTime;	}	public void setCreateTime(String createTime) {	    this.createTime=createTime;	}}